<script setup>
import gsap from 'gsap';
import { MotionPathPlugin } from 'gsap/MotionPathPlugin';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import { onMounted } from 'vue';
import ResponsiveImage from './ResponsiveImage.vue';

gsap.registerPlugin(MotionPathPlugin, ScrollTrigger);

onMounted(() => {
	if (window.innerWidth > 768) {
		const path = document.getElementById('path');
		const pathLength = path.getTotalLength();

		path.style.strokeDasharray = pathLength;
		path.style.strokeDashoffset = pathLength;

		gsap.fromTo(
			'#path',
			{
				strokeDashoffset: pathLength
			},
			{
				strokeDashoffset: 0,
				scrollTrigger: {
					trigger: '#destination',
					start: 'top top',
					end: () => `+=${document.querySelector('#container').scrollWidth}`,
					scrub: 1
				}
			}
		);
		gsap.to('#title', {
			opacity: 0,
			scrollTrigger: {
				trigger: '#destination',
				start: 'top top',
				end: () => `+=${document.querySelector('#container').scrollWidth}`,
				toggleActions: 'play reverse play reverse'
			}
		});
		gsap.set('#plane', { xPercent: -50, yPercent: -50, transformOrigin: '50% 50%' });
		gsap.to('#plane', {
			motionPath: {
				path: '#path',
				align: '#path',
				autoRotate: true
			},
			scrollTrigger: {
				trigger: '#destination',
				start: 'top top',
				end: () => `+=${document.querySelector('#container').scrollWidth}`,
				pin: true,
				scrub: 1
			}
		});
		gsap.from('#plane', {
			opacity: 0,
			scrollTrigger: {
				trigger: '#destination',
				start: 'top top',
				toggleActions: 'play pause play reverse',
				end: () => `+=${document.querySelector('#container').scrollWidth}`
			}
		});
		const places = gsap.utils.toArray('.place');
		places.forEach(place => {
			gsap.to(place, {
				xPercent: -100 * (places.length - 1),
				scrollTrigger: {
					trigger: '#destination',
					start: 'top top',
					end: () => `+=${document.querySelector('#container').scrollWidth}`,
					scrub: 1
				}
			});
		});
		let proxy = { scaleX: 1 },
			scaleXSetter = gsap.quickSetter('.img', 'scaleX'), // fast
			clamp = gsap.utils.clamp(0.9, 1.1); // limit scaleX between 0.95 and 1.05 for a realistic effect.

		ScrollTrigger.create({
			onUpdate: self => {
				let velocity = self.getVelocity(),
					threshold = 100, // increase threshold for smoother scaling
					scaleX;

				// Apply scale only if velocity exceeds the threshold
				if (Math.abs(velocity) > threshold) {
					scaleX = clamp(1 + velocity / 5000); // adjust the divisor for smoother scaling
					// only do something if the scaleX is more severe. Remember, we're always tweening back to 1, so if the user slows their scrolling quickly, it's more natural to just let the tween handle that smoothly rather than jumping to the smaller scaleX.
					if (Math.abs(scaleX - 1) > Math.abs(proxy.scaleX - 1)) {
						proxy.scaleX = scaleX;
						gsap.to(proxy, {
							scaleX: 1,
							duration: 1, // adjust duration for smoothness
							ease: 'power3.out', // adjust ease for a smoother effect
							overwrite: true,
							onUpdate: () => scaleXSetter(proxy.scaleX)
						});
					}
				}
			}
		});

		// make the right edge "stick" to the scroll bar. force3D: true improves performance
		gsap.set('.img', { transformOrigin: 'right', force3D: true });
	} else {
		gsap.to('#small-plane', {
			scrollTrigger: {
				trigger: '.small__wrapper',
				start: '-=80 top',
				end: 'bottom-=100 top',
				scrub: 1,
				pin: true
			}
		});
		gsap.utils.toArray('.small__box').forEach(box => {
			gsap.from(box, {
				scrollTrigger: {
					trigger: box,
					start: 'top center-=200',
					toggleClass: 'active'
				}
			});
		});
	}
});
